import React from 'react';

const Drawer = (props) => {
  const {
    children,
    ...rest
  } = props;

  const[isOpen, toggleDrawer] = React.useState(false); 

  return (
    <div className="Drawer" {...rest}>
      {children}
      <button onClick={() => toggleDrawer(!isOpen)}>
        {isOpen ? 'Close Drawer' : 'Open Drawer'}
      </button>
    </div>
  )
}

export default Drawer;
