import React from 'react';

const Drawer = (props) => {
  const {
    children,
    ...rest
  } = props;

  const[isOpen, toggleDrawer] = React.useState(true); 

  return (
    <div className="Drawer" {...rest}>
      <p>{isOpen ? 'Open' : 'Closed'}</p>
      {children}
    </div>
  )
}

export default Drawer;
